import React, { useState } from 'react';
import { useForm } from 'react-hook-form';
import { useHistory } from 'react-router-dom';
// import { yupResolver } from '@hookform/resolvers/yup';
// import * as yup from "yup";
import { useData } from "./DataContext";
import Joi from "joi-browser";
import { toast } from 'react-toastify';

// const schema = yup.object().shape({
//     firstName: yup.string().matches(/^([^0-9])$/, "First name shpuld not contain numbers").required("First name is required field"),
//     surname: yup.string().matches(/^([^0-9])$/, "surname shpuld not contain numbers").required("surname is required field"),
//     email: yup.string().email("Invalid Email.").required("Email is required"),

// })

export const Step1 = () => {

    const { setValues, data } = useData();
    const [ rawData, setRawData ] = useState("");

    const { register, handleSubmit, errors } = useForm({
        defaultValues: { firstName: data.firstName, surname: data.surname, email: data.email },
        mode: "onBlur",
        // resolver: yupResolver(schema)
    });
    const history = useHistory();

    const schema = {
        firstName: Joi.string().min(1).required(),
        surname: Joi.string().min(2).required(),
        email: Joi.string().email().required(),
    }

    const validate = () => {
        const result = Joi.validate(rawData, schema, {
            abortEarly: false
        });
        if (!result.error) return null;

        const errors = {};
        for (let item of result.error.details)
            errors[item.path[0]] = item.message;

        return errors;
    }

    const onSubmit = (usrData) => {
        // console.log(data);
        setRawData(usrData);
        console.log(usrData);
        let errors = validate();
        console.log(errors);
        if (errors) {
            Object.values(errors).forEach(err => toast.error(err))
            return
        } else {  
            setValues(data)
            console.log(data);
            // history.push("/step2")
        }
    }

    return (
        <div>
            <h2>Step 1: Your details</h2>
        <form onSubmit={handleSubmit(onSubmit)}>
            <input 
                id="firstName"
                name="firstName"
                type="text"
                placeholder="First Name"
                {...register('firstName', { required: true })} 
                  />
            <input {...register('surname', { required: true })} name="surname" type="text" placeholder="Surname" />
            <input {...register('email', { required: true })} name="email" type="email" placeholder="Email" />
            <button type="submit" >Next</button>
        </form>
        </div>
    )
}
