import React,  { useState } from 'react';
import { useForm } from 'react-hook-form';
import { useHistory } from 'react-router-dom';
import { useData } from "./DataContext";

import Joi from "joi-browser";
import { toast } from 'react-toastify';

export const Step2 = () => {

const history = useHistory();

const { setValues, data } = useData();

const { register, handleSubmit, errors } = useForm({
    defaultValues: { 
        phone: data.phone,
        gender: data.gender,
        birth: data.birth
     },
    mode: "onBlur",
});

const schema = {
    phone: Joi.string().min(10).regex(/^[0-9]+$/).required().label("Phone"),
    gender: Joi.string().alphanum().optional().label("Gender"),
    birth: Joi.date().required().optional().label("Birth date"),
}
const validate = (usrData) => {
    const options = { abortEarly: false };
    // let ifNo = {
    //     isNumber:function(str) {
    //         var pattern = /[^a-zA-Z]/g;
    //         return pattern.test(str);
    //     }
    // }
    // if (ifNo.isNumber(usrData.firstName)){
    //     toast.error('Only text allowed')
    //     return 
    // }

    const result = Joi.validate(usrData, schema, options);
    if (!result.error) return null;

    let v_errors = {};
    for (let item of result.error.details) {
        v_errors[item.path[0]] = item.message;
    }
    return v_errors;
}

const onSubmit = (data) => {
    let usrData = data
    let errors = validate(usrData);
    if (errors) {
        Object.values(errors).forEach(err => {
            toast.error(err)
            return
        })
    } else {  
        setValues(data)
        history.push("/step3")
    }
}

    return (
        <div>
            <h2>Step 2: More comments</h2>
        <form onSubmit={handleSubmit(onSubmit)}>
            <input {...register('phone')} name="phone" type="text" placeholder="Phone" />
            <input {...register('gender')} name="gender" type="text" placeholder="Gender" />
            <input {...register('birth')} name="birth" type="email" placeholder="birth" />
            <button type="submit">Next</button>
        </form>
        </div>
    )
}
